# aの値の範囲を指定（例: 0から10まで0.1刻み）
a_values = [i/1000 for i in range(1, 10001)]

# cの値を指定
c_values = 0

# 結果を格納するリスト
results = []

# 各aに対するbの値を求める
for a_val in a_values:
    solution_of_v1_Ab = sp.solve(equation_of_v1_Ab.subs(a, a_val), b)
    solution_of_v1_Ne = sp.solve(equation_of_v1_Ne.subs(a, a_val), b)
    solution_of_v2_Ab = sp.solve(equation_of_v2_Ab.subs(a, a_val), b)
    solution_of_v2_Anl = sp.solve(equation_of_v2_Anl.subs(a, a_val), b)
    solution_of_v2_Ne = sp.solve(equation_of_v2_Ne.subs(a, a_val), b)
    solution_of_v3_Anl = sp.solve(equation_of_v3_Anl.subs(a, a_val), b) 
    solution_of_v3_Ab = sp.solve(equation_of_v3_Ab.subs(a, a_val), b)  
    solution_of_v4_Anl = sp.solve(equation_of_v4_Anl.subs(a, a_val), b)
    if solution_of_v1_Ab and solution_of_v1_Ne and solution_of_v2_Ab and solution_of_v2_Anl and solution_of_v2_Ne and solution_of_v3_Anl and solution_of_v3_Ab and solution_of_v4_Anl:
        positive_solution_of_v1_Ab = [sol.evalf() for sol in solution_of_v1_Ab if sol.evalf() > 0]
        positive_solution_of_v1_Ne = [sol.evalf() for sol in solution_of_v1_Ne if sol.evalf() > 0]
        positive_solution_of_v2_Ab = [sol.evalf() for sol in solution_of_v2_Ab if sol.evalf() > 0]
        positive_solution_of_v2_Anl = [sol.evalf() for sol in solution_of_v2_Anl if sol.evalf() > 0]
        positive_solution_of_v2_Ne = [sol.evalf() for sol in solution_of_v2_Ne if sol.evalf() > 0]
        positive_solution_of_v3_Anl = [sol.evalf() for sol in solution_of_v3_Anl if sol.evalf() > 0]
        positive_solution_of_v3_Ab = [sol.evalf() for sol in solution_of_v3_Ab if sol.evalf() > 0]
        positive_solution_of_v4_Anl = [sol.evalf() for sol in solution_of_v4_Anl if sol.evalf() > 0]
        if positive_solution_of_v1_Ab and positive_solution_of_v1_Ne and positive_solution_of_v2_Ab and positive_solution_of_v2_Anl and positive_solution_of_v2_Ne and positive_solution_of_v3_Anl and positive_solution_of_v3_Ab and positive_solution_of_v4_Anl:
            result_dict = {'LSiSi/LNaNa': a_val, 'v1_Ab': positive_solution_of_v1_Ab, 'v1_Ne': positive_solution_of_v1_Ne, 'v2_Ab': positive_solution_of_v2_Ab, 'v2_Anl': positive_solution_of_v2_Anl, 'v2_Ne': positive_solution_of_v2_Ne, 'v3_Anl': positive_solution_of_v3_Anl, 'v3_Ab': positive_solution_of_v3_Ab, 'v4_Anl': positive_solution_of_v4_Anl}
            results.append(result_dict)

